import { Modal } from 'antd';
import styles from './scheduleModal.css'
import { useEffect, useState } from 'react';
import { getByFilm } from '../../../APIs/sessionAPI';

const ScheduleModal = ({visible, onOk, onCancel, filmId, filmTitle}) => {
  const [sessions, setSessions] = useState([{id: 0}]);

  useEffect(async () => {
    const array = await getByFilm(filmId)
    setSessions([{id: 1}])
  }, [filmId]);

  return(
    <Modal
      onCancel={onCancel}
      onOk={onOk}
      visible={visible}
      title={filmTitle}
      footer={[]}
    >
      <div style={{display: "flex", flexWrap: "wrap"}}>
        <div style={{width: "50%", padding: "10px 20px"}}>Дата</div>
        <div style={{width: "50%", padding: "10px 20px"}}>Время</div>
        <div style={{width: "50%", padding: "20px"}}>20 мая</div>
        <div style={{width: "50%", padding: "20px"}}>3</div>
        <div style={{width: "50%", padding: "20px"}}>21 мая</div>
        <div style={{width: "50%", padding: "20px"}}>3</div>
        <div style={{width: "50%", padding: "20px"}}>22 мая</div>
        <div style={{width: "50%", padding: "20px"}}>3</div>
        <div style={{width: "50%", padding: "20px"}}>23 мая </div>
        <div style={{width: "50%", padding: "20px"}}>3</div>
        {sessions[0].id}
      </div>
    </Modal>
  )
}

export default ScheduleModal;
