import { Button, Form, Input } from 'antd';
import { useState } from 'react';
import { postFilm } from '../../../APIs/filmAPI';

const AddMovie = () => {

  const [title, setTitle] = useState('');
  const [duration, setDuration] = useState('');
  const [coeficient, setCoeficient] = useState(1);
  const [country, setCountry] = useState('');
  const [year, setYear] = useState('');


  const onHandleSubmit = async () => {
    const data = await postFilm(title, duration, coeficient, year, country)
    console.log(data)
  }

  return(
    <Form
      onFocus={onHandleSubmit}
    >
      <Form.Item label="Название">
        <Input value={title} onChange={(e) => setTitle(e.target.value)}/>
      </Form.Item>
      <Form.Item label="Длительность (мин)">
        <Input value={duration} onChange={(e) => setDuration(e.target.value)}/>
      </Form.Item>
      <Form.Item label="Коэфициент">
        <Input value={coeficient} onChange={(e) => setCoeficient(e.target.value)}/>
      </Form.Item>
      <Form.Item label="Страна">
        <Input value={country} onChange={(e) => setCountry(e.target.value)}/>
      </Form.Item>
      <Form.Item label="Год">
        <Input value={year} onChange={(e) => setYear(e.target.value)}/>
      </Form.Item>
      <Form.Item label="Отправить">
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>

  )
}

export default AddMovie;
