import styles from './auditoriumScheme.module.css';
import { useEffect, useState } from 'react';
import { getSeatBySession } from '../../../APIs/seatAPI';
import Seat from './Seat';

const AuditoriumSeats = ({auditoriumId}) => {
  const [seats, setSeats] = useState([]);
  useEffect(async () => {
    setSeats(await getSeatBySession(auditoriumId))
  } ,[auditoriumId])
  return(
    <div className={styles.seats}>
      <button onClick={() => console.table(seats)}>111111</button>
      {seats.map((seat) => (
        <div>
          {seat.map((place) => (
            <div>
              <Seat film={place} />
            </div>
          ))}
        </div>
      ))}
    </div>
  )
}

export default AuditoriumSeats;
