import styles from './OrderInfo.module.css';

const OrderInfo = ({activeSeats}) => {
  return(
    <div className={styles.orderInfo}>
      <div>
        {activeSeats.map((seat) => `${seat.row} ряд ${seat.place} место`)}
      </div>
      <button>Заказать</button>
    </div>
  )
}

export default OrderInfo;
