import { Modal } from 'antd';
import styles from './scheduleModal.css'
import { useEffect, useState } from 'react';
import { getByFilm } from '../../../APIs/sessionAPI';

const ScheduleModal = ({visible, onOk, onCancel, filmId, filmTitle}) => {
  const [sessions, setSessions] = useState([]);
  const [nextDays, setNextDays] = useState(getNextDays(1))

  const getNextDay = (day) => {
    const date = new Date(day)
    const  nextDay = new Date(date);
    nextDay.setDate(day.getDate() + 1);
    return nextDay
  }

  const getNextDays = (a) => {
    if (typeof a === "number") {
      let array = []
      const date = new Date()
      array.push(date.getDay())
      getNextDays(array)
    } else {
      if (a.length === 4) {
        return a
      } else {
        a.push(getNextDay(new Date(a[a.length - 1])))
        getNextDays(a)
      }
    }
  }

  const gg = () => {
    for (const session of sessions) {

    }
  }


  useEffect(async () => {
    const array = await getByFilm(filmId)
    setSessions(array)
    console.log(nextDays)
  }, []);

  return(
    sessions[0] !== undefined ? (
      <Modal
        onCancel={onCancel}
        onOk={onOk}
        visible={visible}
        title={filmTitle}
        footer={[]}
      >
        <div style={{display: "flex", flexWrap: "wrap"}}>
          <div style={{width: "50%", padding: "10px 20px"}}>Дата</div>
          <div style={{width: "50%", padding: "10px 20px"}}>Время</div>
          <div style={{width: "50%", padding: "20px"}}>20 мая</div>
          <div style={{width: "50%", padding: "20px"}}>3</div>
          <div style={{width: "50%", padding: "20px"}}>21 мая</div>
          <div style={{width: "50%", padding: "20px"}}>3</div>
          <div style={{width: "50%", padding: "20px"}}>22 мая</div>
          <div style={{width: "50%", padding: "20px"}}>3</div>
          <div style={{width: "50%", padding: "20px"}}>23 мая </div>
          <div style={{width: "50%", padding: "20px"}}>3</div>
        </div>
      </Modal>
      ):(
      <Modal
        onCancel={onCancel}
        onOk={onOk}
        visible={visible}
        title={filmTitle}
        footer={[]}
      >
        <div style={{display: "flex", flexWrap: "wrap"}}>
          На данный момент нет доступных сеансов
        </div>
      </Modal>
    )
  )
}

export default ScheduleModal;
