const AuditoriumScheme = ({auditoriumId}) => {
  const [films, setFilms] = useState('');

  useEffect(async () => {
    console.log(auditoriumId)
    setFilms(await getFilmByAuditorium(auditoriumId))
  }, [])
  return(
    <div>
      {films.title}
    </div>)
}

export default AuditoriumScheme;
