import styles from './OrderInfo.module.css';

const OrderInfo = ({activeSeats, film, time}) => {
  const convertDate = (iso) => {
    const date = new Date(iso);
    return `${date.getDate()}.${date.getMonth()}.${date.getMonth()} ${date.getHours()}:${date.getMinutes()}`;
  }
  return(
    <div className={styles.orderInfo}>
      <div className={styles.orderHeader}>
        Заказ
      </div>
      <div>Дата: {convertDate(time.time)}</div>
      <div>
        Выбранные билеты:
        {activeSeats.map((seat, i) => i === activeSeats.length - 1 ?
          ` ${seat.row} ряд ${seat.place} место` :
          ` ${seat.row} ряд ${seat.place} место,`)}
      </div>
      <button className={styles.orderButton}>Купить</button>
    </div>
  )
}

export default OrderInfo;
