import FilmInfo from './FilmInfo';

const AuditoriumScheme = ({auditoriumId, film }) => {
  const [auditorium, setAuditorium] = useState(auditoriumId);

  return(
    <div className={styles.layout}>
      <FilmInfo film={film}/>
    </div>)
}

export default AuditoriumScheme;
